CREATE TABLE "users" ("id" BIGSERIAL NOT NULL, "name" VARCHAR NOT NULL, "password" VARCHAR NOT NULL, "email" VARCHAR NOT NULL, "created_at" TIMESTAMPTZ NOT NULL, "updated_at" TIMESTAMPTZ NOT NULL, PRIMARY KEY ("id"));
CREATE TABLE "todos" ("id" BIGSERIAL NOT NULL, "user_id" BIGINT NOT NULL, "content" VARCHAR NOT NULL, "done" BOOLEAN NOT NULL, "until" TIMESTAMPTZ, "created_at" TIMESTAMPTZ NOT NULL, "updated_at" TIMESTAMPTZ NOT NULL, PRIMARY KEY ("id"), FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE RESTRICT ON DELETE RESTRICT);
CREATE UNIQUE INDEX "user_email_idx" ON "users" ("email");
